insert into coveragerecords (identifier_id, data_source_id, date, exception) select i.id, datasources.id, w.presentation_ready_attempt, NULL from datasources, identifiers i join editions e on e.primary_identifier_id=i.id join works w on e.work_id=w.id where w.presentation_ready = true and datasources.name='Library Simplified metadata wrangler' and e.title is not null and e.sort_author is not null;
